import { handleError } from "vue";
import axios from "./instance";

const API_KEY = 'api/v2/';

const API_PATHS = {
    auth: 'users/auth/token/login/',
    user: 'users/employee/current/'
}

const api = {

    error: {},
    contextForMuttations: {},

    async auth(username, password) {

        try {
            const res = await axios.post(API_KEY + API_PATHS['auth'], {
                username,
                password
            });

            return res.data;

        } catch (error) {
            this.errorHandle(error);
            return null;
        }

    },

    async currentUser(token) {
        try {
            const res = await axios.get(API_KEY + API_PATHS['user'], {
                headers: {
                    "Authorization": "Token " + token
                }
            });

            return res.data;

        } catch(error) {
            console.log(error);
            this.errorHandle(error);
            return null;
        }
    },

    errorHandle(error) {

        if (error.response) {
            this.error 
                = error.response.status === 400 ? "Неверный логин/пароль" : error.message;

            switch (error.response.status) {
                case 400:
                    this.error = "Неверный логин/пароль";
                    break;
            }

        } else if (error.request) {

            this.error = "Bad connection";

        } else {

            //

        }    
    }

};

export default api;
